import { Component } from '@angular/core';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {

  isModal = false;

  addItem(modal: boolean)
  {
    this.isModal = modal;
  }

}
<div
  class="task-wrapper"
>
  <div
    class="task-title"
  >
    <h1 class="bebas-font">Tasks</h1>
  </div>

  <h1
    *ngIf="isModal"
  >Hello
  </h1>

  <app-new-task (addNewTask)="addItem($event)"></app-new-task>
</div>
