@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'ng_pomodoro';
}
<div class="timer-wrapper">
  <app-timer></app-timer>
</div>

<h1>Angular Pomodoro Timer</h1>

