import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-task-tab',
  templateUrl: './task-tab.component.html',
  styleUrls: ['./task-tab.component.scss']
})
export class TaskTabComponent {
  @Input() task = {
    "name": "",
    "description": "",
  };

  @Input() pomodoroCount = 0;
  @Input() pomodorosRequired = 1;
  @Input() isCompleted = false;

  ngOnChanges()
  {
    if (this.pomodoroCount >= this.pomodorosRequired)
    {
      this.isCompleted = true;
    }
  }

}
<div
  class="task-wrapper"
>
  <div
    class="task"
  >
    <h1 class="bebas-font">{{ task.name }}</h1>
    <p class="completed-task">{{ pomodoroCount }}/<span>{{ pomodorosRequired }}</span></p>
  </div>
</div>
