import { Component } from '@angular/core';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {
  isModal = false;

  tasks: any[] = [
  ]

  showModal(modal: boolean)
  {
    this.isModal = modal;
  }

  closeModal(modal: boolean)
  {
    this.isModal = modal;
  }

  addNewTask(task: any)
  {
    this.tasks.push({
      "name": task.name,
      "description": task.description,
    });
  }
}
<div
  *ngIf="isModal"
  class="modal-wrapper"
>
  <app-task-modal
    (emitCloseModal)="closeModal($event)"
    (emitAddTask)="addNewTask($event)"
  >
  </app-task-modal>
</div>
<div
  class="task-wrapper"
>
  <div
    class="task-title"
  >
    <h1 class="bebas-font">Tasks</h1>
  </div>
  <app-new-task (newTaskEvent)="showModal($event)"></app-new-task>

  <div
    class="task-list-wrapper"
  >
    <div
      class="task-list"
      *ngFor="let task of tasks"
    >
      <app-task-tab [task]="task"></app-task-tab>
    </div>
  </div>

</div>
